import React, { PureComponent } from 'react'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'
import Slider from 'react-slick'
import './ShopOffers.css'
export class ItemDetailSub extends PureComponent {
  render() {
    var images = [
      { img: 'assets/w1.jpeg' },
      { img: 'assets/w2.jpeg' },
      { img: 'assets/w3.jpeg' },
      { img: 'assets/w4.jpeg' },
      { img: 'assets/w5.jpeg' },
    ]
    var imgSlides = () =>
      images.map((num) => (
        <div className='imgpad'>
          <img className='imgdetails' src={num.img} width='100%' />
        </div>
      ))
    return (
      <div class='card card-flush pt-3 mb-5 mb-xl-10'>
        {/*begin::Card header*/}
        <div class='card-header'>
          {/*begin::Card title*/}
          <div class='card-title'>
            <h2 class='fw-bolder'>24 KT Gold Ring</h2>
          </div>
          {/*begin::Card title*/}

          {/*begin::Card toolbar*/}
          <div class='card-toolbar'>
            <a
              href='../../demo2/dist/apps/subscriptions/add.html'
              class='btn btn-light-primary'
              style={{
                background: '#95203D',
                width: '100%',
                color: '#ffffff',
              }}
            >
              Place Order
            </a>
          </div>
          {/*end::Card toolbar*/}
        </div>
        {/*end::Card header*/}
        {/*begin::Card body*/}
        <div class='card-body pt-3'>
          {/*begin::Section*/}
          <div class='mb-10'>
            {/*begin::Title*/}

            {/*end::Title*/}
            {/*begin::Details*/}
            <div className='row'>
              <div className='col-lg-6'>
                <div className='App'>
                  <Slider
                    dots={false}
                    slidesToShow={3}
                    slidesToScroll={2}
                    autoplay={true}
                    arrows={false}
                    autoplaySpeed={3000}
                  >
                    {imgSlides()}
                  </Slider>
                </div>
              </div>
              <div className='col-lg-6'></div>
            </div>
            {/*end::Row*/}
          </div>

          {/*end::Section*/}
          {/*begin::Section*/}

          {/*end::Section*/}
        </div>
        {/*end::Card body*/}
      </div>
    )
  }
}

export default ItemDetailSub
