import React, { PureComponent } from 'react'
import SubscriptionIntro from './SubscriptionIntro'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'
import Slider from 'react-slick'
import SubscriptionTransactions from './SubscriptionTransactions'
import './ShopOffers.css'
export class ItemDetailSub extends PureComponent {
  render() {
    var images = [
      { img: 'assets/w1.jpeg' },
      { img: 'assets/w2.jpeg' },
      { img: 'assets/w3.jpeg' },
      { img: 'assets/w4.jpeg' },
      { img: 'assets/w5.jpeg' },
    ]
    var imgSlides = () =>
      images.map((num) => (
        <div className='imgpad'>
          <img className='imgdetails' src={num.img} width='100%' />
        </div>
      ))
    return (
      <div class='card card-flush pt-3 mb-5 mb-xl-10'>
        {/*begin::Card header*/}
        <div class='card-header'>
          {/*begin::Card title*/}
          <div class='card-title'>
            <h2 class='fw-bolder'>24 KT Gold Ring</h2>
          </div>
          {/*begin::Card title*/}
          <div class='row'>
            <div class='col-lg-12'>
              <div className='App'>
                <Slider
                  dots={false}
                  slidesToShow={1}
                  slidesToScroll={1}
                  autoplay={true}
                  arrows={false}
                  autoplaySpeed={3000}
                >
                  {imgSlides()}
                </Slider>
              </div>
            </div>
          </div>
          {/*begin::Card toolbar*/}
          <div class='card-toolbar'>
            <a
              href='../../demo2/dist/apps/subscriptions/add.html'
              class='btn btn-light-primary'
              style={{
                background: '#95203D',
                width: '100%',
                color: '#ffffff',
              }}
            >
              Place Order
            </a>
          </div>
          {/*end::Card toolbar*/}
        </div>
        {/*end::Card header*/}
        {/*begin::Card body*/}
        <div class='card-body pt-3'>
          {/*begin::Section*/}
          <div class='mb-10'>
            {/*begin::Title*/}

            {/*end::Title*/}
            {/*begin::Details*/}
            <div class='d-flex flex-wrap py-5'>
              {/*begin::Row*/}
              <div class='flex-equal me-5'>
                {/*begin::Details*/}

                {/*end::Details*/}
              </div>
              {/*end::Row*/}
              {/*begin::Row*/}
              <div class='flex-equal'>
                {/*begin::Details*/}
                <table class='table fs-6 fw-bold gs-0 gy-2 gx-2 m-0'>
                  {/*begin::Row*/}
                  <tr>
                    <td class='text-gray-400 min-w-175px w-175px'>
                      Subscribed To:
                    </td>
                    <td class='text-gray-800 min-w-200px'>
                      12 Months Standard Weight Plan
                    </td>
                  </tr>
                  {/*end::Row*/}
                  {/*begin::Row*/}
                  <tr>
                    <td class='text-gray-400'>Minimum Installment:</td>
                    <td class='text-gray-800'>Rs 5000</td>
                  </tr>
                  {/*end::Row*/}
                  {/*begin::Row*/}
                  <tr>
                    <td class='text-gray-400'>Cycle method:</td>
                    <td class='text-gray-800'>Monthly</td>
                  </tr>
                  {/*end::Row*/}
                  {/*begin::Row*/}
                  <tr>
                    <td class='text-gray-400'>Duration:</td>
                    <td class='text-gray-800'>12 Months</td>
                  </tr>
                  {/*end::Row*/}
                </table>
                {/*end::Details*/}
              </div>
              {/*end::Row*/}
            </div>
            {/*end::Row*/}
          </div>

          {/*end::Section*/}
          {/*begin::Section*/}
          <SubscriptionTransactions />
          {/*end::Section*/}
        </div>
        {/*end::Card body*/}
      </div>
    )
  }
}

export default ItemDetailSub
