import React, { useEffect, useState, Component } from 'react'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'
import Slider from 'react-slick'
import './ShopOffers.css'
function ShopOffers() {
  const [images, SetImages] = useState([])

  useEffect(() => {
    const fetchImages = async () => {
      const response = await fetch('https://goldv2.herokuapp.com/api/slider')
      const ImageDate = await response.json()
      SetImages(ImageDate)
    }
    fetchImages()
  }, [])

  const imgSlides = images.map((num) => {
    return (
      <div className='imgpad'>
        {' '}
        <img className='imgdetails img-fluid' src={num.image} width='10%' />
      </div>
    )
  })

  return (
    <div className='App'>
      <Slider
        dots={false}
        slidesToShow={3}
        slidesToScroll={2}
        autoplay={true}
        arrows={false}
        autoplaySpeed={3000}
      >
        {imgSlides()}
      </Slider>
    </div>
  )
}
export default ShopOffers
