import React, { Component } from 'react'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'
import Slider from 'react-slick'
import './ShopOffers.css'
export class ShopOffers extends Component {
  render() {
    var images = [
      { img: 'assets/w1.jpeg' },
      { img: 'assets/w2.jpeg' },
      { img: 'assets/w3.jpeg' },
      { img: 'assets/w4.jpeg' },
    ]
    var imgSlides = () =>
      images.map((num) => (
        <div className='imgpad'>
          <img className='imgdetails' src={num.img} width='10%' />
        </div>
      ))
    return (
      <div className='App'>
        <Slider
          dots={true}
          slidesToShow={4}
          slidesToScroll={2}
          autoplay={false}
          arrows={true}
          autoplaySpeed={3000}
        >
          {imgSlides()}
        </Slider>
      </div>
    )
  }
}
export default ShopOffers
