import React, { Component } from 'react'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'
import Slider from 'react-slick'
import './ShopOffers.css'
export class ShopOffers extends Component {
  componentDidMount() {
    fetch('https://goldv2.herokuapp.com/api/slider')
      .then((res) => res.json())
      .then((json) => {
        this.setState({
          items: json,
          DataisLoaded: true,
        })
      })
  }
  render() {
    var imgSlides = () =>
      items.map((num) => (
        <div className='imgpad'>
          <img className='imgdetails img-fluid' src={num.image} width='10%' />
        </div>
      ))
    return (
      <div className='App'>
        <Slider
          dots={false}
          slidesToShow={3}
          slidesToScroll={2}
          autoplay={true}
          arrows={false}
          autoplaySpeed={3000}
        >
          {imgSlides()}
        </Slider>
      </div>
    )
  }
}
export default ShopOffers
